              {/*<Image floated='right' size='mini' src='/assets/images/avatar/large/molly.png' />*/}
              <Card.Header>{friend}</Card.Header>
              <Card.Meta>Friend</Card.Meta>
              <Card.Description>
              </Card.Description>
            </Card.Content>
            <Card.Content extra>
